package corejavapart1;

public class FindMax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2,b=5,c=7;
		if(a>b && a>c) {
			System.out.println(a+ "is max");
		}
		else if(b>a && b>c) {
			System.out.println(b+ "is max");
		}
		else {
			System.out.println(c+ "is max");
		}
	}

}
